// Minimal loader for html2pdf library.
// This file dynamically loads the official bundle from a CDN if it's not already present.
(function(){
  if (window.html2pdf) return; // already loaded
  var s = document.createElement('script')
  s.src = 'https://unpkg.com/html2pdf.js@0.9.3/dist/html2pdf.bundle.min.js'
  s.async = true
  s.onload = function(){ console.info('html2pdf loaded from CDN') }
  s.onerror = function(){ console.warn('Failed to load html2pdf from CDN. Replace this file with the real bundle for offline use.') }
  document.head.appendChild(s)
})();
